INSERT INTO `subway_site` (`id`, `site_pic`, `site_introduced`, `site_code`, `line_name`, `oper_code`, `line_code`, `site_name`) VALUES (280, 'http://www.szmc.net/styles/index/zdWeb/images/1009_b.jpg', '光雅园站位于深圳市龙岗区雅园路与五和南路交叉口，东南侧市政绿地内，车站主体呈南北走向，南接南坑站，北接五和站。\n光雅园站色彩基调以橙色为主，为地下三层车站，地下一层为站厅层，地下二层为设备层，地下三层为站台层，站台形式为岛式站台。车站设置有5个出入口，车站周边为商业办公区及住宅区。', '240020', '10', '1240020000', '240', '光雅园');
INSERT INTO `subway_site` (`id`, `site_pic`, `site_introduced`, `site_code`, `line_name`, `oper_code`, `line_code`, `site_name`) VALUES (281, 'http://www.szmc.net/styles/index/zdWeb/images/1013_b.jpg', '华为站位于深圳市龙岗区坂雪岗大道和稼先路交叉口，车站主体呈南北走向，南接贝尔路站，北接岗头站。\n华为站色彩基调以白色为主，为地下二层车站，地下一层为站厅层，地下二层为站台层，站台形式为岛式站台。车站设置有6个出入口，临近深圳华为坂田园区，周边为成熟配套生活区。', '240024', '10', '1240024000', '240', '华为');
INSERT INTO `subway_site` (`id`, `site_pic`, `site_introduced`, `site_code`, `line_name`, `oper_code`, `line_code`, `site_name`) VALUES (282, 'http://www.szmc.net/styles/index/zdWeb/images/1001_b.jpg', '福田口岸站位于深圳市福田区桂花路南侧和福田口岸大楼西侧的下方，车站主体呈南北走向，东接4号线福田口岸站，北接福民站。\n福田口岸站是10号线与4号线的换乘站（需出站换乘）,车站色彩基调以香槟色为主，为地下三层车站，地下一层为站厅层，地下二层为设备层，地下三层为站台层，站台形式为岛式站台。车站设置有4个出入口，临近福田口岸出境大厅、福田口岸公交总站，周边为成熟配套生活区。', '240011', '10', '1240011000', '240', '福田口岸');
